mapper.select:
select s.*, (
	select avg(rated) from shopreview where shopnum = s.num
) as avgRated
from
		(select t.*, rownum as rn
		from (
			select * from shop
		) t
	) s
	where rn between #{start} and #{end}

mapper.update:
update shop set name=#{name} , cate=#{cate} , 
tel=#{tel}, loc_x=#{loc_x}, loc_y=#{loc_y}
where num = #{num}
